mapper.select:
SELECT user_id, user_pw, user_name, user_phone, user_gender, user_email
	FROM user
WHERE user_id = #{param1}

mapper.update:
UPDATE user
SET user_name = #{user_name}, user_phone = #{user_phone}, user_email = #{user_email}
WHERE user_id = #{user_id}

UPDATE user
SET user_pw = #{param2}
WHERE user_id = #{param1}

UPDATE user
SET user_status = 0
WHERE user_id = #{user_id}
